# -.- coding:utf-8 -.-# __author__ = 'zhengtong'import randomclass MySort:    def __init__(self, start, end, count):        self.start = start        self.end = end        self.count = count        self.random_data = []    #生成指定范围指定数量的随机数    def _generator(self):        for i in range(0, self.count):            self.random_data.append(random.uniform(self.start, self.end))    def sort(self):        self._generator()        #取长度值        n = len(self.random_data)        for i in range(0, n):            #每次用第一位跟剩下的数值比对            for j in range(1,n-i):                #如果j-1的数值大于j的数值                if self.random_data[j-1] > self.random_data[j]:                    #那么数值小的排在前面大的排在后面                    self.random_data[j-1], self.random_data[j] = self.random_data[j], self.random_data[j-1]        return self.random_data        return random_dataif __name__ == '__main__':        sorted_data = MySort(10, 1000, 100)        data = sorted_data.sort()        for a in data:            print(a)