# -.- coding:utf-8 -.-# __author__= 'vivi'class Student(object):    @property    def score(self):        return self._score    @score.setter    def score(self, value):        if not isinstance(value, int):            raise ValueError('score must be an iteger')        if value < 0 or value > 100:            raise ValueError('score must between 0 - 100!')s = Student()s.score = 60print('s.score= ', s.score)s.score = 9999